// +---------------------+
// Id | userId | roomId
// +---------------------+

const connection = () => {
  try {
    if (connectionPool !== undefined) {
      return connectionPool
    }
    connectionPool = mysql.createPool({
      host: db.host,
      user: db.user,
      password: db.password,
      database: db.name,
      port: db.port
    })
    return connectionPool
  } catch (err) {
    console.error(err)
